class RouteFactory
{
    /**
     * @param array $methods
     * @param string $path
     * @param mixed $action
     * @param string|null $name
     * @return Route
    */
    public function createRoute(
        array $methods,
        string $path,
        mixed  $action,
        ?string $name = null
    ): Route {

        return new Route($methods, $path, $action, $name);
    }




    /**
     * @param string $methods
     * @param string $path
     * @param mixed $action
     * @param string|null $name
     * @return Route
    */
    public function createRouteFromStringMethods(
        string $methods,
        string $path,
        mixed $action,
        ?string $name = null
    ): Route {
        return $this->createRoute(
            explode('|', $methods),
            $path,
            $action,
            $name
        );
    }
}
